mapper.select:
select * from ( select rownum RNUM, zxcvb.* from ( select m.m_no AS "m_no", m.mid AS "mid", m.name AS "name", m.nickname as "nickname", m.email as "email", m.birthdate as "birthday", m.phone as "phone", m.gender as "gender", s.sido_id AS "sido", si.sigungu_id AS "sigungu", m.detail_address AS "detail_address", to_char(m.reg_date, 'YYYY-MM-DD') AS "reg_date" , to_number(to_char(sysdate, 'YYYY')) - to_number(to_char(birthdate, 'YYYY')) AS age
,pr.games_played as "games_played" ,pr.draws as "draws" , pr.wins as "wins" ,pr.losses as "losses" ,pr.goals_for as "goals_for" ,pr.goals_assist as "goals_assist"
FROM member m LEFT JOIN sido s ON m.sido_id = s.sido_id LEFT JOIN sigungu si ON m.sigungu_id = si.sigungu_id LEFT JOIN player_record pr ON m.m_no = pr.player
where m.m_no != 1
order by m.m_no asc
order by ${sort}
) zxcvb) where RNUM >= #{begin_rowNo} and RNUM <= #{end_rowNo}

mapper.update:
		update member set

		name = #{name}
		,mid = #{mid}
		,nickname=#{nickname}
		,email = #{email}
		,birthdate = #{birthday}

		,phone = #{phone}

		,gender
		=#{gender}
		,sido_id = #{sido_id}
		,sigungu_id = #{sigungu_id}
		,detail_address = #{detail_address}

		where m_no = #{m_no}

		update player_record set

		games_played = #{games_played}
		,wins=#{wins}
		,
		draws = #{draws}
		,losses = #{losses}
		,goals_for =
		#{goals_for}
		,goals_assist =
		#{goals_assist}

		where player = #{player}

mapper.select:
select * from ( select rownum RNUM, zxcvb.* from (
select b_no AS "b_no", subject AS "subject", writer AS "writer", content AS "content", to_char(reg_date, 'YYYY-MM-DD') AS "reg_date", readcount AS "readcount" FROM noticeboard
order by
b_no desc
order by
${sort}
) zxcvb) where RNUM >= #{begin_rowNo} and RNUM <= #{end_rowNo}

select b_no "b_no" ,subject "subject" ,writer "writer" ,to_char(reg_date, 'YYYY-MM-DD') AS "reg_date" ,readcount "readcount" ,content "content"
from noticeboard
where b_no=#{b_no}

mapper.insert:
insert into noticeboard(
b_no
,subject
,writer
,readcount
,content
)
values ( (select nvl(max(b_no),0)+1
from noticeboard)
,#{subject}
,#{writer}
,0
,#{content}
)

mapper.update:
		update noticeboard set

		subject = #{subject}
		,writer = #{writer}
		,content
		=
		#{content}

		where b_no = #{b_no}
