mapper.select:
select * from ( select rownum RNUM, zxcvb.* from (
select m_no AS "m_no", mid AS "mid", name AS "name", nickname as "nickname", to_char(reg_date, 'YYYY-MM-DD') AS "reg_date" FROM member where m_no != 1
order by
m_no desc
order by
${sort}
) zxcvb) where RNUM >= #{begin_rowNo} and RNUM <= #{end_rowNo}

mapper.update:
		update member set

		name = #{name}
		,mid = #{mid}
		,nickname=#{nickname}
		,
		email = #{email}
		,birthdate = #{birthday}
		,phone = #{phone}
		,detail_address =
		#{detail_address}

		where m_no = #{m_no}

		update player_record set

		games_played = #{games_played}
		,wins=#{wins}
		,
		draws = #{draws}
		,losses = #{losses}
		,goals_for =
		#{goals_for}
		,goals_assist =
		#{goals_assist}

		where player = #{player}

mapper.select:
select * from ( select rownum RNUM, zxcvb.* from (
select b_no AS "b_no", subject AS "subject", writer AS "writer", content AS "content", to_char(reg_date, 'YYYY-MM-DD') AS "reg_date", readcount AS "readcount" FROM noticeboard
order by
b_no desc
order by
${sort}
) zxcvb) where RNUM >= #{begin_rowNo} and RNUM <= #{end_rowNo}

select b_no "b_no" ,subject "subject" ,writer "writer" ,to_char(reg_date, 'YYYY-MM-DD') AS "reg_date" ,readcount "readcount" ,content "content"
from noticeboard
where b_no=#{b_no}

mapper.insert:
insert into noticeboard(
b_no
,subject
,writer
,readcount
,content
)
values ( (select nvl(max(b_no),0)+1
from noticeboard)
,#{subject}
,#{writer}
,0
,#{content}
)

mapper.update:
		update noticeboard set

		subject = #{subject}
		,writer = #{writer}
		,content
		=
		#{content}

		where b_no = #{b_no}
